    public int maximumGap(final List<Integer> A) {
        //wrong answer
        int max = 0;
        int size = A.size();
        int i = 0;
        int j = size-1;

        while(i<j)
        {
            int diff = j-i;
            if(max < diff && A.get(i) <= A.get(j))
            {
                System.out.println(A.get(i) + " " + A.get(j) + " " + i + " " + j);
                max = diff;
                break;
            }
            if(A.get(i+1)<=A.get(j)) i++;
            else if(A.get(i)<=A.get(j-1))j--;
            else {
                i++;
            }
        }
        i = 0;
        j = size-1;
        while(i<j)
        {
            int diff = j-i;
            if(max < diff && A.get(i) <= A.get(j))
            {
                System.out.println(A.get(i) + " " + A.get(j) + " " + i + " " + j);
                max = Math.max(max, diff);
            }
            if(A.get(i+1)<=A.get(j)) i++;
            else if(A.get(i)<=A.get(j-1))j--;
            else {
                j--;
            }
        }
        return max;
    }

